import React from 'react';
import Header from '../../components/header/Header';
import axios from 'axios';
import './style.css';

class CadastrotrodeEmpresas extends React.Component {

    state = {
        name: '',
        address: '',
        body: '',
        website: '',
        posts: []
    };



    componentDidMount = () => {
        this.getPost();
    };



    getPost = () => {
        axios.get('/companies')
        .then((response)=>{
          const data = response.data;
          this.setState({posts: data})
          console.log("Data has been received");
        })
        .catch(()=> {
          alert("error retriving data");
        });
    
    }


    handleChange = ({target}) => {
        const {name, value} = target;
    
        this.setState({ [name]: value});
    
    };




    submit= (event) => {
        event.preventDefault();
    
        const payload = {
          name: this.state.name,
          address: this.state.address,
          body: this.state.body,
          website: this.state.website,

        };
    
    
        axios({
          url: '/cadastroempresa/save',
          method: 'POST',
          data: payload
        })
        .then(() => {
          console.log('Data has been sent to the server');
          this.resetUserInputs();
          this.getPost();
        })
        .catch(()=> {
          console.log('Internal server error');
        });
    

    };


    resetUserInputs= () => {
        this.setState({
          name:'',
          address: '',
          body:'',
          website: ''
        });
    
    };


    displayPost = (posts) => {

        if(!posts.length) return null;
    
        return posts.map((post, index)=> (
          <div key={index} className="post_display">
            <h3>{post.title}</h3>
            <p>{post.address}</p>
            <p>{post.body}</p>
            <p>{post.website}</p>
          </div>
        ));
    
    }





    render() {
        return (
            <div>
                <Header></Header>

                <div className="form-section">
                    <h1>Registre a sua empresa</h1>

                    <form onSubmit={2}>
                        <div className="form-input">
                            <input
                            type="text"
                            name="name"
                            value=""
                            placeholder="nome da empresa"
                            onChange={this.handleChange}>
                            </input>
                        </div>

                        <div className="form-input">
                            <input
                            type="text"
                            name="address"
                            value=""
                            placeholder="endereço"
                            onChange={this.handleChange}>
                            </input>
                        </div>

                        <div className="form-input">
                            <input
                            type="text"
                            name="body"
                            value=""
                            placeholder="Descreva os serviços que a sua empresa oferece"
                            onChange={this.handleChange}>
                            </input>
                        </div>

                        <div className="form-input">
                            <input
                            type="text"
                            name="website"
                            value=""
                            placeholder="Digite o website da empresa"
                            onChange={this.handleChange}>
                            </input>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default CadastrotrodeEmpresas;
