import React, { useState, useEffect } from 'react';
import {Redirect} from 'react-router-dom';
import { useDispatch, connect } from 'react-redux';
import { signin } from './../redux/actions/companyActions';
import './login.css';

import {
    TOGGLE_SUCCESS
} from '../redux/types/companyTypes';


function Login({companyError, companySuccess}) {

    const[ company, setCompany ] = useState({
        email: "",
        password: ""
    });

    const dispatch = useDispatch();

    const {email, password} = company;

    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(false);


    useEffect(() => {
        if ( companyError && companyError !== null ) {
            setError(companyError);
        }
        if(companySuccess) {
            setSuccess(companySuccess);
            dispatch({type: TOGGLE_SUCCESS})
        }
    }, [companyError, companySuccess, dispatch]);


    function showError() {
        return error && <div className="alert alert-danger">{error}</div>
    };


    function redirectUser(){
        return success && <Redirect to="/"></Redirect>

    };


    function handleInputChange(event) {
        setCompany({...company, [event.target.name]: event.target.value});
    };
    

    function handleFormSubmit(event){
        event.preventDefault();
        dispatch(signin(company));
    };

    return (
        <div className="my_account">

            <div className="container">
                <div className="form">
                    <h2>Login</h2>
                    <div className="input">
                        <div className="inputbox">
                            <label>Email</label>
                            <input 
                            type="text" 
                            placeholder="mail@email.com"
                            onChange={(event)=> handleInputChange(event)} 
                            type="email" 
                            name= "email"
                            required
                            className="form-control"
                            value={email}></input>
                        </div>
                        <div className="inputbox">
                            <label>Password</label>
                            <input 
                            type="password" placeholder="..."
                            onChange={(event)=> handleInputChange(event)} 
                            type="password" 
                            name= "password"
                            required
                            className="form-control"
                            value={password}></input>
                        </div>
                        <div className="inputbox">
                            <input type="submit" value="Sign In"></input>
                        </div>
                    </div>
                    <p className="forget">Forgot password? CLICK HERE</p>
                </div> 

            </div>

        </div>
    );

}

export default Login;
