import React from 'react';
import {Link, withRouter, useHistory } from 'react-router-dom';
import { isAuthenticated, signout  } from './../../help/index';
import './style.css';


function Header ({ currentCompany }) {
    let history = useHistory();

    console.log(isAuthenticated());

    return (
        <header className="header">
            <Link exact to="/">
            <div className="logo_container">
                <strong className="logo">LogisTech</strong>
            </div>
            </Link>

            {!isAuthenticated() && (
                <div className="header_direita">
                    <Link exact to="/login">
                    <div className="login">
                        <span>Log in</span>
                    </div>
                    </Link>

                    <Link exact to="/register">
                    <div className="cadastro_empresa">
                        <span>Register</span>
                    </div>
                    </Link>

                    <Link>
                    <div className="contato">
                        <span>Contact</span>
                    </div>
                    </Link>
                </div>

            )}

            {isAuthenticated() && (
                <div>
                    <Link to={`/company/${isAuthenticated().company._id}`}  
                    className="nav-link"
                    activeStyle={{
                        fontWeight: "bold",
                        color: "#a4f5e5"
                    }}
                    href="#">
                    {`${isAuthenticated().branduser.brandname|| isAuthenticated().branduser.branduser.brandname}´s profile`} 
                    </Link> 

                    <Link
                    onClick={()=> signout(()=> history.push('/'))}>
                        <div className="contato">
                            <span>log out</span>
                        </div>
                    </Link>
                </div>

            )}
        </header>
    );
}

export default Header;
